import React, { Component } from 'react';
import { BiHome } from "react-icons/bi";
import { Nav, Navbar, NavDropdown, Form, Button } from 'react-bootstrap';
import validator from "validator";
import './PartnerForm.css';
import Axios from "axios";

class PartnerForm extends Component {

    state = {
        email: '', //froom sql/sessions
        fullname: '', //from sql/sesisons
        companyname: '', //from sessions/sql
        ProfileStatus: '', //sql to check if user profile is completed
        JobTitle: '',
        position_level: '',
        required_yrs: '', //requirements
        job_scope: '', //highlights to be display on the front
        description: '',
        location: '',
        salary: '',
        qualifaction: '',
        skills: '',
        additional_requirements: ''

    }


    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }


    saveAndContinue = (e) => {
        if (
            !validator.isEmpty(this.state.num) &
            !validator.isEmpty(this.state.businessname) &
            !validator.isEmpty(this.state.password)

        ) {
            e.preventDefault()
            console.log(this.state)
            Axios.post('http://localhost:3001/', this.state).then((Response) => {
                console.log(Response)
            })
        } else {
            var required = document.querySelectorAll("input[required]");

            required.forEach(function (element) {
                if (element.value.trim() === "") {
                    element.style.backgroundColor = "#ffcccb";
                } else {
                    element.style.backgroundColor = "white";
                }
            });

        }
    };



    render() {
        const { email, fullname, companyname, ProfileStatus, JobTitle, position_level, required_yrs, job_scope, description, location, salary, qualification, skills, additional_requirements } = this.state
        return (
            <div className='Container'>
                <div className='form_navbar'>
                    <Navbar bg="dark" variant="dark">
                        <Nav.Link href="./Partner">
                            <div className="image_icon_nav">
                                <BiHome />
                            </div>
                        </Nav.Link>
                        <Nav className="me-auto">
                            <Nav.Link href="./PartnerJobAd">Job Ad</Nav.Link>
                            <Nav.Link href="./PartnerUserSearch">Search user</Nav.Link>
                            <Nav.Link href="./PartnerStats">Statics</Nav.Link>
                        </Nav>
                        <Navbar.Collapse className="justify-content-end">
                            <NavDropdown title={"Sign in as : " + fullname} id="basic-nav-dropdown">
                                <NavDropdown.Item href="./PartnerProfile">Edit profile</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="#action/3.4">Log out</NavDropdown.Item>
                            </NavDropdown>
                        </Navbar.Collapse>
                    </Navbar>
                </div>

                <div className='form_partner'>

                    <p style={{ marginTop: 10, color: 'red' }}>{ProfileStatus}</p>
                    <h2>Job Ad details</h2>
                    <Form>
                        <Form.Group controlId="JobTitle" style={{ marginTop: 20 }} >
                            <Form.Label className="label">Job Title</Form.Label>
                            <Form.Control 
                                style={{ width: 320, color: "black" }}
                                type="text"
                                value={JobTitle}
                                name="JobTitle"
                                required
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Form.Group controlId="position_level" style={{ marginTop: 10 }}>
                            <Form.Label>Position Level</Form.Label>
                            <Form.Control as="select" name="position_level" defaultValue={position_level} onChange={this.props.handleChange} required>
                                <option selected disabled value="">Please select a option</option>
                                <option value="Full Time">Full Time</option>
                                <option value="Part Time">Part Time</option>
                                <option value="Contract">Contract</option>
                                <option value="Temporary">Temporary</option>
                                <option value="Internship">Internship</option>
                            </Form.Control>
                        </Form.Group>

                        <Form.Group controlId="required_yrs" style={{ marginTop: 10 }}>
                            <Form.Label>Required Years of Experience</Form.Label>
                            <Form.Control as="select" name="required_yrs" defaultValue={required_yrs} onChange={this.props.handleChange} required>
                                <option selected disabled value="">Please select a option</option>
                                <option value="1">1</option>
                                <option value="2">2</option>
                                <option value="3">3</option>
                                <option value="4">4</option>
                                <option value="5">5</option>
                                <option value="6">6</option>
                                <option value="7">7</option>
                                <option value="8">8</option>
                                <option value="9">9</option>
                                <option value="10">10</option>
                                <option value="10+">10+</option>
                            </Form.Control>
                        </Form.Group>

                        <Form.Group controlId="job_scope" style={{ marginTop: 10 }} >
                            <Form.Label className="job_scope">Job Highlights</Form.Label>
                            <Form.Control 
                                style={{ width: 320, color: "black" , height: 100}}
                                type="textarea"
                                value={job_scope}
                                name="job_scope"
                                required
                                autoComplete='off'
                                onChange={this.handleChange}
                            />
                        </Form.Group>




                        <Button type="submit" variant="primary" onClick={this.saveAndContinue} style={{ marginTop: 25, marginLeft: 120 }}>Confirm</Button>

                    </Form>


                </div>


            </div>
        );
    }
}

export default PartnerForm;
