import React, { Component } from 'react';
import { BiHome } from "react-icons/bi";
import { Nav, Navbar, NavDropdown } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { Form, Button, Container } from 'react-bootstrap';
import validator from "validator";
import './PartnerForm.css';
import Axios from "axios";

class PartnerForm extends Component {

    state = {
        email: '', //froom sql/sessions
        fullname: '', //from sql/sesisons
        companyname: '', //from sessions/sql
        ProfileStatus: '', //sql to check if user profile is completed
        JobTitle: '',
        JobRole: '',
        position_level: '', 
        required_yrs: '', //requirements
        job_scope: '',  
        description: '',
        location: '',
        salary: '',
        qualifaction: '',
        skills: '',
        additional_requirements: ''

    }


    handleChange = (event) => {
        this.setState({ [event.target.name]: event.target.value })
    }


    saveAndContinue = (e) => {
        if (
            console.log(this.state),
            !validator.isEmpty(this.state.num) &
            !validator.isEmpty(this.state.businessname) &
            !validator.isEmpty(this.state.password)

        ) {
            e.preventDefault()
            console.log(this.state)
            Axios.post('http://localhost:3001/', this.state).then((Response) => {
                console.log(Response)
            })
        } else {
            var required = document.querySelectorAll("input[required]");

            required.forEach(function (element) {
                if (element.value.trim() === "") {
                    element.style.backgroundColor = "#ffcccb";
                } else {
                    element.style.backgroundColor = "white";
                }
            });

        }
    };



    render() {
        const { email, fullname, companyname, ProfileStatus, JobTitle, JobRole, position_level, required_yrs, job_scope, description, location, salary, qualification, skills, additional_requirements } = this.state
        return (
            <div className='Container'>
                <div className='form_navbar'>
                    <Navbar bg="dark" variant="dark">
                        <Nav.Link href="./Partner">
                            <div className="image_icon_nav">
                                <BiHome />
                            </div>
                        </Nav.Link>
                        <Nav className="me-auto">
                            <Nav.Link href="./PartnerJobAd">Job Ad</Nav.Link>
                            <Nav.Link href="./PartnerUserSearch">Search user</Nav.Link>
                            <Nav.Link href="./PartnerStats">Statics</Nav.Link>
                        </Nav>
                        <Navbar.Collapse className="justify-content-end">
                            <NavDropdown title={"Sign in as : " + fullname} id="basic-nav-dropdown">
                                <NavDropdown.Item href="./PartnerProfile">Edit profile</NavDropdown.Item>
                                <NavDropdown.Divider />
                                <NavDropdown.Item href="#action/3.4">Log out</NavDropdown.Item>
                            </NavDropdown>
                        </Navbar.Collapse>
                    </Navbar>
                </div>

                <div className='form_partner'>

                    <p style={{ marginTop: 10, color: 'red' }}>{ProfileStatus}</p>
                    <h4>Job details</h4>
                    <Form>
                        <Form.Group controlId="formEmail" style={{ marginTop: 20 }} >
                            <Form.Label className="label">Email Address</Form.Label>
                            <Form.Control style={{ width: 300 }}
                                type="email"
                                value={email}
                                name="email"
                                required
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Form.Group controlId="formFullName" style={{ marginTop: 10 }}>
                            <Form.Label className="label">Contact Person Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={fullname}
                                name="fullname"
                                required
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Form.Group controlId="formNum" style={{ marginTop: 10 }}>
                            <Form.Label>Contact Number</Form.Label>
                            <Form.Control
                                type="number"
                                value={num}
                                name="num"
                                required
                                minlength={8}
                                min={0}
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Form.Group controlId="formBusinessName" style={{ marginTop: 10 }}>
                            <Form.Label className="label">Registered Business Name</Form.Label>
                            <Form.Control
                                type="text"
                                //pattern="[0-9]{5}"
                                value={businessname}
                                name="businessname"
                                required
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Form.Group controlId="formPassword" style={{ marginTop: 10 }}>
                            <Form.Label className="label">Password</Form.Label>
                            <Form.Control
                                type="password"
                                value={password}
                                name="password"
                                required
                                minlength={8}
                                onChange={this.handleChange}
                            />
                        </Form.Group>

                        <Button type="submit" variant="primary" onClick={this.saveAndContinue} style={{ marginTop: 25, marginLeft: 120 }}>Next</Button>

                    </Form>

                    <Link to="../Login/login" style={{ marginTop: 20 }}>Already a Partner?</Link>
                </div>


            </div>
        );
    }
}

export default PartnerForm;
