import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { Form, Button, Container } from 'react-bootstrap';

class YouthDetails1 extends Component{

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    };


    render() {
        return( <Container>
            <h1>This is the Youth Register page</h1>
            <br></br>
                    <Form>
                        <Form.Group controlId="formEmail">
                                <Form.Label className="label">Email Address</Form.Label>
                                <Form.Control
                                type="email"
                                defaultValue={this.props.inputValues.email}
                                name="email"
                                required
                                onChange={this.props.handleChange}
                                />
                                <br></br>
                        </Form.Group>

                        <Form.Group controlId="formFullName">
                                <Form.Label className="label">Full Name</Form.Label>
                                <Form.Control
                                type="text"
                                defaultValue={this.props.inputValues.fullname}
                                name="fullname"
                                required
                                onChange={this.props.handleChange}
                            />
                            <br></br>
                        </Form.Group>
                        

                        <Form.Group controlId="formPassword">
                            <Form.Label className="label">Password</Form.Label>
                            <Form.Control
                            type="password"
                            defaultValue={this.props.inputValues.password}
                            name="password"
                            required
                            onChange={this.props.handleChange}
                            />
                            <br></br>
                        </Form.Group>
                        
                        <Button variant="primary" onClick={this.saveAndContinue}>Next</Button>
                        
                    </Form>
                    <span style={{ marginLeft: "100px" }}>
                    <Link to="../login">Already a Youth?</Link>
                    </span>
                    
                </Container>
        );
    }
}

export default YouthDetails1;
