import React, { Component } from 'react';
import { Link } from "react-router-dom";
import './YouthLogin.css';
import Axios from "axios";

class Login extends Component {

    
    state = {
          Setpassword: "",
          Setemail: "",
          LoginStatus : ""
        }
      

      login = () => {
          Axios.post("http://localhost:3001/ClientLogin",{
              email : Setemail,
              password : Setpassword
          }).then((response) => {

            if(response.data.message){
                LoginStatus(response.data.message)
            }else{
                LoginStatus("Login Successfully")
            }
          });
      }

    render() {
        return (
                <div className='container'>
                    <div className='header'>
                        <h3 className='headertext'>Youth Login</h3>
                        <p>Find the right job with us</p>
                        <hr className='hr'></hr>
                    </div>

                    <input type="text" onChange={(e) => { Setemail(e.target.value) }} placeholder="Email" />
                    
                    <input type="password" placeholder="Password"  onChange={(e) => { Setpassword(e.target.value) }}/>
                    <button type="submit" onClick={login} >Login</button>
                    <Link to="../Register/YouthRegister">Sign up here</Link>

                    <h1>(LoginStatus)</h1>
                </div>
                
        );
    }
}
