import React, { Component } from 'react';
import {Link} from "react-router-dom";
import { Form, Button, Container } from 'react-bootstrap';
import { Formik, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';


class YouthDetails1 extends Component{

    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
    validationSchema() {
        return Yup.object().shape({
          fullname: Yup.string().required('Fullname is required'),
          email: Yup.string()
            .required('Email is required')
            .email('Email is invalid'),
          password: Yup.string()
            .required('Password is required')
            .min(6, 'Password must be at least 6 characters')
            .max(40, 'Password must not exceed 40 characters'),
        });
      }

      handleSubmit(data) {
        console.log(JSON.stringify(data, null, 2));
      }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

   /* saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
   };
*/

    render() {


        return( 
        <Container>
            <h1>This is the Youth Register page</h1>
            <br></br>
                    <Formik
                    State={this.state}
                    validationSchema={this.validationSchema}
                    onSubmit={this.handleSubmit}
                    >
                     {({ resetForm }) => (
                     <Form>
                        <Form.Group controlId="formEmail" style={{ marginTop: 10}} required >
                                <Form.Label className="label">Email Address</Form.Label>
                                <Form.Control
                                className="form-control"
                                type="email"
                                defaultValue={this.props.inputValues.email}
                                name="email"
                                required
                                onChange={this.props.handleChange}
                                />
                                <ErrorMessage
                                    name="email"
                  component="div"
                  className="text-danger"
                />
                        </Form.Group>

                        <Form.Group controlId="formFullName" style={{ marginTop: 10}}>
                                <Form.Label className="label">Full Name</Form.Label>
                                <Field  
                                className="form-control"
                                type="text"
                                defaultValue={this.props.inputValues.fullname}
                                name="fullname"
                                onChange={this.props.handleChange}
                            />
                            <ErrorMessage
                                name="fullname"
                                component="div"
                                className="text-danger"
                            />
                        </Form.Group>
                        

                        <Form.Group controlId="formPassword" style={{ marginTop: 10}}>
                            <Form.Label className="label">Password</Form.Label>
                            <Form.Control
                            type="password"
                            defaultValue={this.props.inputValues.password}
                            name="password"
                            required
                            onChange={this.props.handleChange}
                            />
                        </Form.Group>
                        
                        <Button type="submit" variant="primary" onClick={this.saveAndContinue} style={{ marginTop: 25}}>Next</Button>
                        <Button type="button" onClick={resetForm} className="btn btn-warning float-right">Reset</Button>
                    </Form>
                     )}
                        </Formik>
                    <Link to="../login" style={{ marginTop: 20}}>Already a Youth?</Link>
                    
                </Container>
        );
    }
}

export default YouthDetails1;
